import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function Account(props) {
    let { login, DangXuat } = props;
    const [email, setEmail] = useState("");
    const [checkLogin, setCheckLogin] = useState(false);
    const handeDx = () => {
        DangXuat(true);
    }
    useEffect(() => {
        getDataLocal();

    }, [])
    const getDataLocal = async () => {
        let res = await localStorage.getItem('email');
        let checkLogin = await localStorage.getItem('checkLogin');
        setEmail(res);
        setCheckLogin(checkLogin)

    }
    console.log(email);
    console.log(setCheckLogin);
    return (
        <div className="dropdown" onClick={handeDx}>

            <li
                className="nav-item account"
                type="button"
                data-toggle="dropdown"
            >
                <Link to={login ? '/login' : '/'} className="btn btn-secondary rounded-circle"
                    style={{ 'backgroundImage': 'url("../images/nhan-vien-giao-nhan.png") ' }}>
                    <i className="fa fa-user" />

                </Link>
                <Link
                    className="nav-link text-dark text-uppercase"
                    to={login ? '/login' : '/'}
                    style={{ display: "inline-block" }}
                >

                    {login ? 'Đăng Nhập' : 'Đăng Xuất'}


                </Link>


            </li>

        </div>
    )
}

export default Account
