import React, { useState, useEffect } from 'react';
import Logo from './Header/Logo';
import Search from './Header/Search';
import Account from './Header/Account';
import Cart from './Header/Cart';
import Tieude from './tieude/Tieude';
import Danhmuc from './ListSp/Danhmuc';
import Sliders from './ListSp/Slider';
import SachHay from './Sachhay/SachHay';
import SachHot from './Sachhot/SachHot';
import SachSapRa from './SachSapRa/SachSapRa';
import SachMoi from './Sachmoi/SachMoi';
import Footer from './Foooter/Footer';

import axios from '../Api-Server/Sach';

function Home() {

    const [sachMoi, setSachMoi] = useState([]);
    const [sachgiamgia, setSachgiamga] = useState([]);

    useEffect(() => {
        getSach();

    });

    const getSach = async () => {
        try {
            let ressachmoi = await axios.get("sachmoi");
            let ressachgiamgia = await axios.get("sachgiamgia");

            setSachMoi(ressachmoi.data);
            setSachgiamga(ressachgiamgia.data);

        } catch (e) {
            console.log("co loi roi sep oi", e);
        }

    }


    return (
        <>

            <div id="fb-root" />
            {/* header */}
            <nav className="navbar navbar-expand-md bg-white navbar-light">
                <div className="container">
                    {/* logo  */}
                    <Logo></Logo>

                    {/* navbar-toggler  */}

                    <div className="collapse navbar-collapse" id="collapsibleNavId">
                        {/* form tìm kiếm  */}
                        <Search></Search>
                        {/* ô đăng nhập đăng ký giỏ hàng trên header  */}
                        <ul className="navbar-nav mb-1 ml-auto">
                            <Account></Account>
                            <Cart></Cart>
                        </ul>
                    </div>
                </div>
            </nav>
            {/* thanh tieu de "danh muc sach" + hotline + ho tro truc tuyen */}
            <section className="duoinavbar">
                <Tieude></Tieude>
            </section>
            {/* noi dung danh muc sach(categories) + banner slider */}
            <section className="header bg-light">
                <div className="container">
                    <div className="row">
                        <Danhmuc></Danhmuc>
                        {/* banner slider  */}
                        <Sliders></Sliders>
                    </div>
                </div>
            </section>
            <SachMoi sachMoi={sachMoi}></SachMoi>
            {/* khoi sach moi  */}
            <SachHay></SachHay>

            <SachHot></SachHot>


            {/* div _1khoi -- khoi sachnendoc */}
            <SachSapRa></SachSapRa>



            {/* thanh cac dich vu :mien phi giao hang, qua tang mien phi ........ */}
            <Footer></Footer>

        </>

    )
}

export default Home
