import React, { useState, useEffect } from 'react';
import Logo from '../Home/Header/Logo';
import Search from '../Search/Search';
import Account from '../Home/Header/Account';
import Cart from '../Home/Header/Cart';
import Tieude from '../Home/tieude/Tieude';
import Footer from '../Home/Foooter/Footer';
import { Link } from 'react-router-dom';
import axios from '../Api-Server/Sach';

function YourCart(props) {
    let { sachMua } = props;
    const [amount, setAmount] = useState(1);
    const [sach, setSach] = useState([]);
    const [listSach, setListSach] = useState([])
    useEffect(() => {
        getSach();

    }, []);
    const getSach = async () => {
        try {
            let resSach = await axios.get("sach");
            setSach(resSach.data);

        } catch (e) {
            console.log("co loi roi sep oi", e);
        }
    }
    const exits = sach.find(x => x.title === sachMua.title);
    console.log(exits);



    const tinhtong = (sachMua) => {
        let tongtien = 0;
        for (let i = 0; i < sachMua.length; i++) {
            tongtien += parseInt(sachMua[i].prince) * amount;
        }
        return tongtien;
    }
    const handleUp = () => {
        let value = parseInt(amount);
        setAmount(value + 1)
    }
    const handleDown = () => {
        if (amount > 1) {
            setAmount(amount - 1)
        }
    }
    return (
        <>
            <div id="fb-root" />
            <nav className="navbar navbar-expand-md bg-white navbar-light">
                <div className="container">
                    <Logo></Logo>
                    <div className="collapse navbar-collapse" id="collapsibleNavId">
                        <Search></Search>
                        <ul className="navbar-nav mb-1 ml-auto">
                            <Account ></Account>
                            <Cart></Cart>
                        </ul>
                    </div>
                </div>
            </nav>
            <section className="duoinavbar">
                <Tieude></Tieude>
            </section>
            <>
                <section className="content my-3" style={{ minHeight: "400px" }}>
                    <div className="container">
                        <div className="cart-page bg-white">
                            <div className="row">
                                {/* giao diện giỏ hàng khi không có item  */}
                                <div className="col-md-10 cart">
                                    <div className="cart-content py-3 pl-3">
                                        {sachMua && <h4 className="header-gio-hang">
                                            GIỎ HÀNG CỦA BẠN CÓ <span>{sachMua.length} SẢN PHẨM</span>
                                        </h4>
                                        }
                                        {sachMua && sachMua.length > 0 && sachMua.map((item) => {
                                            return (
                                                <>
                                                    <div className="cart-list-items" key={item.id}>
                                                        <div className="cart-item d-flex">
                                                            <Link to="#" className="img">
                                                                <img src={item.image}
                                                                    className="img-fluid" alt="anhsp1" />
                                                            </Link>
                                                            <div className="item-caption d-flex w-100">
                                                                <div className="item-info ml-3">
                                                                    <h5 className="ten">
                                                                        {item.title}
                                                                    </h5>
                                                                    <div className="soluong d-flex">
                                                                        <div className="input-number input-group mb-4">
                                                                            <div className="input-group-prepend">
                                                                                <span className="input-group-text btn-spin btn-dec"
                                                                                    onClick={handleDown}
                                                                                >-</span>
                                                                            </div>
                                                                            <input
                                                                                type="text"
                                                                                defaultValue={1}
                                                                                className="soluongsp  text-center"
                                                                                // value={amount}
                                                                                onChange={(e) => setAmount(e.target.value)}
                                                                            />
                                                                            <div className="input-group-append">
                                                                                <span className="input-group-text btn-spin btn-inc"
                                                                                    onClick={handleUp}
                                                                                >+</span>
                                                                            </div>
                                                                        </div>
                                                                    </div>
                                                                </div>
                                                                <div className="item-price ml-auto d-flex flex-column align-items-end">
                                                                    <div>Đơn Giá</div>
                                                                    <div className="giamoi">{item.prince}₫</div>
                                                                </div>

                                                            </div>
                                                        </div>

                                                    </div>
                                                </>
                                            )
                                        })}
                                        <div className="item-price ml-auto d-flex flex-column align-items-end">
                                            <div>Tổng Tiền:{tinhtong(sachMua)} 000 Đ</div>
                                            <button>Thanh Toán</button>
                                        </div>

                                    </div>
                                </div>

                            </div>

                        </div>

                    </div>

                </section>

            </>


            <Footer></Footer>
        </>

    )
}

export default YourCart
