import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Search(props) {
    let { sach, kqSearch } = props;
    const [search, setSearch] = useState("");
    let kq;
    if (sach) {

        kq = sach.filter(item => item.title.toLowerCase().includes(search.toLocaleLowerCase()));
    }
    const handleSearch = () => {
        kqSearch(kq);
        console.log(kqSearch(kq));

    }




    return (
        <form className="form-inline ml-auto my-2 my-lg-0 mr-3">
            <div className="input-group" style={{ width: 520 }}>
                <input
                    type="text"
                    className="form-control"
                    aria-label="Small"
                    placeholder="Nhập sách cần tìm kiếm..."
                    value={search}
                    onChange={e => setSearch(e.target.value)}
                />
                <div className="input-group-append">
                    {search === "" && <button
                        type="button"
                        className="btn"
                        style={{ backgroundColor: "#1196cf ", color: "white " }}

                    >
                        Tìm Kiếm
                    </button>}
                    {search !== "" && <button
                        type="button"
                        className="btn"
                        style={{ backgroundColor: "#1196cf ", color: "white " }}
                        onClick={(kq) => handleSearch(kq)}
                    >
                        <Link to="/search" style={{ textDecoration: "none", color: "white" }} > Tìm Kiếm</Link>
                    </button>}

                </div>
            </div>
        </form>
    )
}

export default Search
