import React, { useState } from 'react';
import './App.css';
import Home from './Home/Home';
import Index from './Infor/Index';
import YourCart from './YourCart/YourCart';
import { Routes, Route } from "react-router-dom";
import Login from './Accout/Login';
import Allsp from './AllSp/Allsp';
import SearchCpn from "./Search/KqSearch";
import Dangki from './Accout/dangki';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


function App() {

  const [sachMua, setSachMua] = useState([]);
  const [ketquaTk, setKetquaTk] = useState([]);


  const ketquaSearch = (item) => {
    if (item) {
      setKetquaTk(item);
    }
  }
  let children = [];

  const getSachMua = (item) => {
    children = children.concat(item);
    // console.log("chill", children);

    // let arr = sachMua.push(children);
    // setSachMua([(sachMua), ]);
    // const myJSON = JSON.stringify(sachMua);
    // console.log(myJSON);
    console.log(sachMua);


  }
  return (
    <>
      <Routes>
        <Route path="/" element={<Home ketquaSearch={ketquaSearch} />} />
        <Route path="/infor/:id" element={<Index buySach={getSachMua} />} />
        <Route path="/cart" element={<YourCart />} />
        <Route path="/allsp/:type" element={<Allsp />} />
        <Route path="/search" element={<SearchCpn ketquaTk={ketquaTk} />} />
        <Route path="/login" element={<Login />} />
        <Route path="/dangki" element={<Dangki />} />
      </Routes>

      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />


    </>

  )
}

export default App;

// vấn đề:
// +chưa truyền được dữ liệu sang cpn tìm kiếm
// + Login render chưa đúng
// + truyền props từ sản phẩm sang giỏ hàng có vấn đề
