import React, { useState, useEffect } from 'react';
import './App.css';
import Home from './Home/Home';
import Index from './Infor/Index';
import YourCart from './YourCart/YourCart';
import { Routes, Route } from "react-router-dom";
import Login from './Accout/Login';
import Allsp from './AllSp/Allsp';
import SearchCpn from "./Search/Search";
import Dangki from './Accout/dangki';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
function App() {
  const [buySach, setBuySach] = useState([]);
  const [login, setLogin] = useState(true);
  const DangXuat = (item) => {
    setLogin(item)
  }
  const sachMua = (item) => {
    if (item) {
      setBuySach(item);
    }
    console.log(buySach);
  }

  return (
    <>
      <Routes>
        <Route path="/" element={<Home login={login} DangXuat={DangXuat} />} />
        <Route path="/infor/:id" element={<Index login={login} sachMua={sachMua} />} />
        <Route path="/cart" element={<YourCart login={login} buySach={buySach} />} />
        <Route path="/allsp/:type" element={<Allsp login={login} />} />
        <Route path="/search" element={<SearchCpn login={login} />} />
        <Route path="/login" element={<Login login={DangXuat} />} />
        <Route path="/dangki" element={<Dangki />} />
      </Routes>
      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
      {/* Same as */}
      <ToastContainer />
    </>

  )
}

export default App;

// vấn đề:
// +chưa truyền được dữ liệu sang cpn tìm kiếm
// + Login render chưa đúng
// + truyền props từ sản phẩm sang giỏ hàng có vấn đề
