import React, { useState, useEffect } from 'react';
import { useMemo } from "react";


function Search(props) {
    let { sach } = props;
    const [search, setSearch] = useState("");

    const counttries = useMemo(() => {
        if (!search) {
            return sach;
        }
        return sach.filter((item) => {
            return item.title.toLowerCase().inCludes(search.toLocaleLowerCase());

        })


    }, [search, sach])
    console.log(counttries);

    return (
        <form className="form-inline ml-auto my-2 my-lg-0 mr-3">
            <div className="input-group" style={{ width: 520 }}>
                <input
                    type="text"
                    className="form-control"
                    aria-label="Small"
                    placeholder="Nhập sách cần tìm kiếm..."
                    value={search}
                    onChange={e => setSearch(e.target.value)}
                />
                <div className="input-group-append">
                    <button
                        type="button"
                        className="btn"
                        style={{ backgroundColor: "#1196cf ", color: "white " }}
                    >
                        Tìm Kiếm

                    </button>

                </div>
            </div>
        </form>
    )
}

export default Search
